package com.liyongquan.weeklycontest.wc292;

import lombok.extern.slf4j.Slf4j;
import org.junit.Assert;
import org.junit.Test;

import static org.junit.Assert.*;

@Slf4j
public class CountTextsTest {
    private CountTexts countTexts = new CountTexts();

    @Test
    public void countTexts() {
        int res = countTexts.countTexts("444479999555588866");
        Assert.assertEquals(3136, res);
    }

    //""
    @Test
    public void testCountTexts() {
        String s = "";
        int res = countTexts.countTexts(s);
        Assert.assertEquals(886136986, res);
    }
}